mapper.insert:
insert into wedding (
id,
data,
deleted
)
values (
#{ id,jdbcType=BIGINT },
#{ data,jdbcType=LONGVARCHAR },
#{ deleted,jdbcType=INTEGER }
)

mapper.update:
