mapper.insert:
INSERT INTO gamemaster(username, userid, serverid, gmlevel, addtimes, date, idDeleted) 
VALUES(#{username, jdbcType=VARCHAR}
	, #{userid, jdbcType=BIGINT}
	, #{serverid, jdbcType=INTEGER}
	, #{gmlevel, jdbcType=INTEGER}
	, #{addtimes, jdbcType=BIGINT}
	, #{date, jdbcType=VARCHAR}
	, 0
	)

mapper.update:
UPDATE gamemaster SET 
	gmlevel=#{gmlevel, jdbcType=INTEGER}, idDeleted=#{isDeleted, jdbcType=INTEGER}  
WHERE userid=#{userid, jdbcType=BIGINT}
